import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MonacoEditor from '@monaco-editor/react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import './Quiz.css';

const CodeQuiz = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [score, setScore] = useState(0);
    const [timer, setTimer] = useState(30);
    const [code, setCode] = useState('');
    const [feedback, setFeedback] = useState('');
    const [quizCompleted, setQuizCompleted] = useState(false);

    const navigate = useNavigate();  // Initialize navigate function for redirection

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/quiz');
                setQuestions(res.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
            }
        };
        fetchQuestions();
    }, []);

    useEffect(() => {
        const timerId = setInterval(() => {
            setTimer((prevTimer) => prevTimer - 1);
        }, 1000);

        return () => clearInterval(timerId);
    }, [timer]);

    const handleAnswer = (isCorrect) => {
        if (isCorrect) setScore(score + 1);
        handleNextQuestion();
    };

    const handleNextQuestion = () => {
        setTimer(30);
        setCurrentQuestion(currentQuestion + 1);
    };

    const handleCodeSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:5000/api/verify', { code });
            setFeedback(response.data.feedback);
        } catch (error) {
            console.error('Error verifying code:', error);
        }
    };

    useEffect(() => {
        if (currentQuestion >= questions.length) {
            setQuizCompleted(true);
        }
    }, [currentQuestion, questions.length]);

    if (!questions.length) return <p>Loading questions...</p>;

    return (
        <div className="quiz-container">
            <div className="quiz-header-container">
                <p className="timer">Time Left: {timer}s</p>
            </div>

            {quizCompleted ? (
                <div className="quiz-completion">
                    <h2 className="quiz-header">Quiz Completed!</h2>
                    <p className="quiz-score">Your score: {score}/{questions.length}</p>
                    <button
                        className="back-home-btn"
                        onClick={() => navigate('/')}  // Redirect to the home page
                    >
                        Back to Home
                    </button>
                </div>
            ) : (
                <>
                    <h2 className="quiz-question">{questions[currentQuestion].question}</h2>

                    <div className="code-editor">
                        <MonacoEditor
                            height="400px"
                            language="javascript"
                            value={code}
                            onChange={(newValue) => setCode(newValue)}
                            theme="vs-dark"
                        />
                    </div>

                    <button onClick={handleCodeSubmit}>Submit Code</button>
                    <div className="feedback">{feedback}</div>
                </>
            )}
        </div>
    );
};

export default CodeQuiz;
