import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MonacoEditor from '@monaco-editor/react';
import { useNavigate } from 'react-router-dom';
import './Quiz.css';

const CodeQuiz = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [score, setScore] = useState(0);
    const [timer, setTimer] = useState(30);
    const [quizCompleted, setQuizCompleted] = useState(false);  // Track quiz completion
    const navigate = useNavigate(); // For navigation to home

    // Fetch questions from the API
    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/quiz');  // Adjust your endpoint
                setQuestions(res.data);  // Save the questions
            } catch (err) {
                console.error('Error fetching questions:', err);
            }
        };
        fetchQuestions();
    }, []);

    // Timer functionality for each question
    useEffect(() => {
        if (timer <= 0) {
            handleNextQuestion(); // Move to the next question when timer hits 0
        } else {
            const timerId = setInterval(() => {
                setTimer((prevTimer) => prevTimer - 1);
            }, 1000);
            return () => clearInterval(timerId);  // Cleanup the timer
        }
    }, [timer]);

    // Handle answer selection (correct or incorrect)
    const handleAnswer = (isCorrect) => {
        if (isCorrect) setScore(score + 1);  // Increment score if answer is correct
        handleNextQuestion();  // Move to the next question
    };

    // Move to the next question
    const handleNextQuestion = () => {
        if (currentQuestion < questions.length - 1) {
            setTimer(30);  // Reset timer for the next question
            setCurrentQuestion(currentQuestion + 1);  // Go to the next question
        } else {
            setQuizCompleted(true);  // Set quizCompleted to true after the last question
        }
    };

    // Check if questions have been loaded and handle the display logic
    if (!questions.length) return <p>Loading questions...</p>;  // Display loading text while fetching questions

    return (
        <div className="quiz-container">
            <div className="quiz-header-container">
                <p className="timer">Time Left: {timer}s</p>
            </div>

            {quizCompleted ? (
                <div className="quiz-completion">
                    <h2 className="quiz-header">Quiz Completed!</h2>
                    <p className="quiz-score">Your score: {score}/{questions.length}</p>
                    <button
                        className="back-home-btn"
                        onClick={() => navigate('/')}  // Navigate to the home page
                    >
                        Back to Home
                    </button>
                </div>
            ) : (
                <>
                    <h2 className="quiz-question">{questions[currentQuestion].question}</h2>
                    <div className="quiz-options">
                        {questions[currentQuestion].options.map((option, index) => (
                            <button
                                key={index}
                                className="quiz-option"
                                onClick={() => handleAnswer(index === questions[currentQuestion].correctOption)}  // Correct answer check
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default CodeQuiz;
