import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Quiz.css'; // Add custom CSS for further styling
import congratulationsImage from '../assets/Congrats.png';

const Quiz = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [score, setScore] = useState(0);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/quiz');
                setQuestions(res.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
            }
        };
        fetchQuestions();
    }, []);

    const handleAnswer = (isCorrect) => {
        if (isCorrect) setScore(score + 1);
        setCurrentQuestion(currentQuestion + 1);
    };

    if (!questions.length) return <p>Loading questions...</p>;

    if (currentQuestion >= questions.length) {
        return (
            <div className="quiz-container">
                <h1 className="text-center">Quiz Completed!</h1>
                <h2 className="text-center">Your Score: {score}/{questions.length}</h2>
                <img
                    src={congratulationsImage}
                    alt="Congratulations"
                    className="img-fluid mx-auto d-block"
                />
            </div>
        );
    }

    return (
        <div className="quiz-container">
            <h1 className="text-center">Quiz App</h1>
            <div className="question-card">
                <h3>{questions[currentQuestion].question}</h3>
                <div className="options">
                    {questions[currentQuestion].options.map((option, index) => (
                        <button
                            key={index}
                            className="btn btn-primary btn-lg btn-block"
                            onClick={() => handleAnswer(index === questions[currentQuestion].correctOption)}
                        >
                            {option}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Quiz;
